<?php
namespace LTPet\Pets\WalkingPets;

use pocketmine\network\protocol\PlayerListPacket;
use pocketmine\network\protocol\AddPlayerPacket;
use pocketmine\network\protocol\EntityEventPacket;
use pocketmine\level\particle\HeartParticle;
use pocketmine\utils\UUID;
use pocketmine\entity\Entity;
use pocketmine\entity\Effect;
use pocketmine\item\Item;
use pocketmine\Player;
use pocketmine\level\Level;

use LTPet\Main;
/*
*	NPC类
*/
class LTNPC extends WalkingPet{
	const NETWORK_ID = 63;

	public $width = 0.6;
	public $height = 1.8;
	public $length = 0.5;
	public $name = '女仆';
	public $eyeHeight = 1.62;
	protected $uid = null;
	protected $spluttered = null;
	protected $splutteredCountdown = 0;
	protected $splutteredCount = 0;
	public function __construct($level, $nbt, Player $owner, $att){
		parent::__construct($level,$nbt, $owner, $att);
		$this->uid=UUID::fromRandom();
	}
	public function getSpluttered(){
		return $this->spluttered;
	}
	public function linkEntity(Player $player){//啪啪啪
		if($this->getSpluttered()===null) {
			if($player->getPleasureEvent()){
				return $player->sendMessage('§c你正在跟你的伴侣啪啪啪呢！');
			}
			if($player->getLinkedEntity()){
				return $player->sendMessage('§c你当前是骑乘状态 无法完成这个操作!');
			}
			if($this->getAtt()->getLove()<=10){
				return $player->sendMessage('§c亲密度不足！');
			}
			if($player!==$this->getOwner()){
				if($player->getMoney()<10000){
					return $player->sendMessage('§c你的钱不够啪啪啪别人女仆！');
				}
				$player->reduceMoney(10000, '强奸女仆');
                $player->sendMessage('§c你的啪啪啪别人的女仆，女仆收费10000已扣除！');
				$this->getOwner()->sendMessage('§l§a['.$this->getPetName().'§r§a]§d主人我卖淫为你增加了10000收入~');
				$this->getOwner()->addMoney(10000, '女仆收入');
                $this->getOwner()->newProgress('肮脏的收入');//新成就
			}
            $this->spluttered = $player;
            $player->teleport($this, $player->getYaw(), $player->getPitch(), false);
            $this->setDataProperty(29, self::DATA_TYPE_POS, [$this->x, $this->y, $this->z]);
            $this->setDataFlag(27, 1, true, self::DATA_TYPE_BYTE);//躺下
			$player->addEffect(Effect::getEffect(10)->setDuration(20*20)->setAmplifier(4));
            $player->teleport($this, $player->getYaw(), $player->getPitch(), false);
			$player->setDataFlag(Entity::DATA_FLAGS, Entity::DATA_FLAG_IMMOBILE, true);//禁止移动
			$this->splutteredCountdown=100+$this->getAtt()->getLove()*10;//啪啪啪时间 主人5秒加好感度
			
			$player->setLinkedEntity($this);
            return;
        }
	}
	public function onUpdate($currentTick){
		if($this->getSpluttered()!==null){//如果是啪啪啪状态
			if(!($this->owner instanceof Player) or $this->owner->closed) {
				$this->close();
				return false;
			}
			if($this->closed) {
				return false;
			}
			$player=$this->getSpluttered();
			if($this->getServer()->getTick()%2===0){
				$player->setSneaking(true);//潜行
				$pk = new EntityEventPacket();
				$pk->event = EntityEventPacket::HURT_ANIMATION;//受伤动画
				$pk->eid=$this->getId();
				if(!mt_rand(0, 2)){
					foreach($this->getViewers() as $p){
						$p->dataPacket($pk);
						if($p===$this->owner){
							$p->sendMessage('§l§a['.$this->getPetName().'§r§a]§d'.self::CallBed());//叫声..
						}else{
							$p->sendMessage('§l§a['.$this->owner->getName().'的女仆'.$this->getPetName().'§r§a]§d'.self::CallBed());//叫声..
						}
					}
				}
				$player->getLevel()->addParticle(new HeartParticle($player));//爱心粒子
			}
			$this->splutteredCount++;
			if(--$this->splutteredCountdown<=0){
				if($this->splutteredCount<=200){//如果时间小于10秒
					$player->sendMessage('§l§a['.$this->owner->getName().'的女仆'.$this->getPetName().'§r§a]§d你太快了吧！');
				}
				$this->stopPapa();
			}
		}
		return parent::onUpdate($currentTick);
	}
    public function stopPapa()
    {
		if($this->getSpluttered()!==null){
		    /** @var Player $player */
			$player=$this->getSpluttered();
			if ($player === $this->getOwner()){
                $player->newProgress('感觉都一样');
            }else{
                $player->newProgress('追求刺激');
            }
			$player->removeEffect(10);
			$player->setLinkedEntity(null);
			$this->spluttered = null;
			$this->splutteredCountdown = 0;
			$this->splutteredCount = 0;
			$player->setDataFlag(Entity::DATA_FLAGS, Entity::DATA_FLAG_IMMOBILE, false);//允许移动
			$this->setDataProperty(29, self::DATA_TYPE_POS, [0, 0, 0]);
			$this->setDataFlag(27, 1, false, self::DATA_TYPE_BYTE);
		}
    }
	public function spawnTo(Player $player){
		if(isset($this->hasSpawned[$player->getLoaderId()]) or !isset($player->usedChunks[Level::chunkHash($this->chunk->getX(), $this->chunk->getZ())]))return;
		$info=$this->getAtt()->getInfo();
		if(!isset($info['skin']))$info['skin']='';
		$this->getAtt()->setInfo($info);
		if($info['skin']!==''){
			$this->server->updatePlayerListData($this->uid, $this->getId(), '§d'.$this->owner->getName().'的女仆§e'.$this->getPetName(), 'Standard_CustomSlim', Main::getInstance()->skins[$info['skin']]);
		}else{
			$this->server->updatePlayerListData($this->uid, $this->getId(), '§d'.$this->owner->getName().'的女仆§e'.$this->getPetName(), 'Standard_CustomSlim', base64_decode(''));
		}
		$pk = new AddPlayerPacket();
		$pk->uuid = $this->uid;
		$pk->username = $this->getPetName();
		$pk->eid = $this->getId();
		$pk->x = $this->x;
		$pk->y = $this->y+$this->getEyeHeight();
		$pk->z = $this->z;
		$pk->speedX = 0;
		$pk->speedY = 0;
		$pk->speedZ = 0;
		$pk->yaw = $this->yaw;
		$pk->pitch = $this->pitch;
		$pk->item = Item::get(0);
		$pk->metadata = $this->dataProperties;     
		$player->dataPacket($pk);
		$this->hasSpawned[$player->getLoaderId()] = $player;
	}
    public function close()
    {
        if($this->closed)return;
		$this->server->removePlayerListData($this->uid);
        parent::close();
    }
